import TelegramBot from 'node-telegram-bot-api';

const token = process.env.TELEGRAM_BOT_TOKEN;

if (!token) {
  console.log('Telegram bot token not found. Bot will not start.');
  process.exit(0);
}

// Create a bot without polling in Replit environment
const bot = new TelegramBot(token, { 
  polling: false
});

// Bot kommandalari
bot.onText(/\/start/, (msg) => {
  const chatId = msg.chat.id;
  const firstName = msg.from?.first_name || 'Foydalanuvchi';

  const welcomeMessage = `Xush kelibsiz, Planify Up!

Bu bot sizga yordam beradi:

ğŸ“ Kunlik vazifalarni boshqarish
ğŸ¯ Maqsadlar qo'yish va amalga oshirish
ğŸ“Š Progressingizni kuzatish
ğŸ† Yutuqlarga erishish

Mini ilovaga kirish uchun pastdagi tugmani bosing! ğŸ‘‡`;

  const options = {
    reply_markup: {
      inline_keyboard: [
        [
          {
            text: 'ğŸš€ Sayohatni boshlash',
            web_app: {
              url: process.env.REPLIT_DOMAINS?.split(',')[0] 
                ? `https://${process.env.REPLIT_DOMAINS.split(',')[0]}`
                : 'https://your-replit-domain.replit.app'
            }
          }
        ]
      ]
    }
  };

  bot.sendMessage(chatId, welcomeMessage, options);
});

bot.onText(/\/help/, (msg) => {
  const chatId = msg.chat.id;

  const helpMessage = `ğŸ¯ **Planify Up - Sizning shaxsiy assistentingiz**

â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”â”

**ğŸ® Asosiy komandalar:**
ğŸš€ /start - Ilovani ishga tushirish
ğŸ†˜ /help - Batafsil yo'riqnoma

**ğŸ“± Ilovadan foydalanish:**
âœ¨ "ğŸš€ Sayohatni boshlash" tugmasini bosing
ğŸŒ Til (O'zbek/Rus/Ingliz) ni Profil bo'limidan tanlang
â• Yangi vazifa qo'shish - "+" tugmasini bosing
ğŸ“Š Statistikada progressingizni kuzating
ğŸ¯ Maqsadlar yarating va ularni amalga oshiring
ğŸ“… Kunlik rejalaringizni tuzib boring

**ğŸ† Imkoniyatlar:**
â€¢ Vazifalarni kategoriyalar bo'yicha guruhlash
â€¢ Maqsadlar uchun progress tracking
â€¢ Achievement tizimi orqali motivatsiya
â€¢ Har kuni yangi challenge'lar
â€¢ Shaxsiy statistika va tahlillar

**ğŸ”§ Yordam kerakmi?**
Muammo bo'lsa, /start ni qayta ishlatib ko'ring
Planify Up - sizning muvaffaqiyat kalit! ğŸ—ï¸âœ¨`;

  bot.sendMessage(chatId, helpMessage);
});

// Xato holatlarini boshqarish
bot.on('polling_error', (error) => {
  console.log('Polling error:', error);
});

bot.on('webhook_error', (error) => {
  console.log('Webhook error:', error);
});

// Webhook endpoint qo'shamiz
export const setupBotWebhook = (app: any) => {
  const webhookPath = `/bot${token}`;

  app.post(webhookPath, (req: any, res: any) => {
    bot.processUpdate(req.body);
    res.sendStatus(200);
  });

  // Set webhook URL if in production
  if (process.env.REPLIT_DOMAINS) {
    const domain = process.env.REPLIT_DOMAINS.split(',')[0];
    const webhookUrl = `https://${domain}${webhookPath}`;

    bot.setWebHook(webhookUrl).then(() => {
      console.log(`Telegram bot webhook set to: ${webhookUrl}`);
    }).catch((error) => {
      console.log('Failed to set webhook:', error.message);
    });
  }
};

console.log('Telegram bot configured successfully! ğŸ¤–');

export { bot };

