  --sidebar-border: hsl(217.2, 32.6%, 17.5%);
  --sidebar-ring: hsl(239, 84%, 67%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

.app-container {
  width: 100%;
  max-width: 450px;
  margin: 0 auto;
  min-height: 100vh;
  padding-bottom: 80px; /* Space for bottom navigation */
  padding-left: 8px;
  padding-right: 8px;
}

@media (max-width: 400px) {
  .app-container {
    padding-left: 4px;
    padding-right: 4px;
  }

  .glass-effect {
    padding: 12px 8px;
  }

  .text-lg {
    font-size: 1rem;
  }

  .text-xl {
    font-size: 1.125rem;
  }

  .grid-cols-3 {
    grid-template-columns: repeat(3, minmax(0, 1fr));
    gap: 8px;
  }

  /* Fix select width on small screens */
  .w-32 {
    width: 6rem;
  }

  /* Fix text overflow */
  .text-ellipsis {
    text-overflow: ellipsis;
    overflow: hidden;
    white-space: nowrap;
  }
}

/* Text overflow fixes */
.text-overflow-fix {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
  max-width: 100%;
}

/* Button mobile fixes */
.button-mobile {
  min-width: 0;
  flex: 1;
  word-wrap: break-word;
}

/* Navigation text fix */
.nav-text {
  max-width: 60px;
  font-size: 0.7rem;
  line-height: 1;
}

/* Card content padding fixes for mobile */
@media (max-width: 400px) {
  .card-content-mobile {
    padding: 12px 8px;
  }

  .grid-mobile {
    gap: 8px;
  }

  .text-responsive {
    font-size: 0.875rem;
  }
}

.glass-effect {
  backdrop-filter: blur(10px);
  background: hsl(var(--card) / 0.8);
}

.progress-ring {
  transform: rotate(-90deg);
}

.progress-ring-circle {
  stroke-dasharray: 283;
  stroke-dashoffset: 283;
  transition: stroke-dashoffset 0.35s;
}

.slide-up {
  animation: slideUp 0.3s ease-out;
}

@keyframes slideUp {
  from {
    transform: translateY(20px);
    opacity: 0;
  }
  to {
    transform: translateY(0);
    opacity: 1;
  }
}

.bounce-in {
  animation: bounceIn 0.6s ease-out;
}

@keyframes bounceIn {
  0% { transform: scale(0.3); opacity: 0; }
  50% { transform: scale(1.05); }
  70% { transform: scale(0.9); }
  100% { transform: scale(1); opacity: 1; }
}

